mapper.update:
UPDATE private_chat_unread
SET unread_count = 0
WHERE user_id = #{userId}
  AND contact_id = #{contactId}

mapper.select:
SELECT unread_count
FROM private_chat_unread
WHERE user_id = #{userId}
  AND contact_id = #{contactId}

SELECT COALESCE(SUM(unread_count), 0)
FROM private_chat_unread
WHERE user_id = #{userId}
