mapper.update:
UPDATE user_info
nick_name = #{nickName}, sex = #{sex}, status = #{status}, last_login_time = #{lastLoginTime}, last_off_time = #{lastOffTime},
WHERE user_id = #{userId}

mapper.select:
SELECT  FROM user_info


        AND nick_name LIKE CONCAT('%', #{nickName}, '%')


        AND status = #{status}


ORDER BY create_time DESC
LIMIT #{offset}, #{pageSize}

SELECT COUNT(*) FROM user_info


        AND nick_name LIKE CONCAT('%', #{nickName}, '%')


        AND status = #{status}
